mapper.update:
UPDATE signer_nonce_allocation
SET status = 'RELEASED',
    locked_until = NULL,
    updated_at = #{now},
    reason = '超时回收',
    fencing_token = #{fencingToken}
WHERE signer = #{signer}
  AND status = 'HELD'
  AND locked_until IS NOT NULL
  AND locked_until < #{expireBefore}
  AND fencing_token <= #{fencingToken}

mapper.insert:
INSERT INTO signer_nonce_allocation(
    signer, nonce, status, locked_until, fencing_token, updated_at, created_at
)
VALUES(
    #{signer}, #{nonce}, 'HELD', #{lockedUntil}, #{fencingToken}, #{updatedAt}, #{createdAt}
)
ON CONFLICT(signer, nonce)
DO UPDATE SET
    status = 'HELD',
    locked_until = #{lockedUntil},
    fencing_token = #{fencingToken},
    updated_at = #{updatedAt}
WHERE signer_nonce_allocation.status != 'CONSUMED'
  AND signer_nonce_allocation.fencing_token <= #{fencingToken}

mapper.update:
UPDATE signer_nonce_allocation
SET status = 'CONSUMED',
    tx_hash = #{txHash},
    locked_until = NULL,
    updated_at = #{now},
    fencing_token = #{fencingToken}
WHERE id = #{id}
  AND status = 'HELD'
  AND fencing_token <= #{fencingToken}

UPDATE signer_nonce_allocation
SET status = 'RELEASED',
    tx_hash = NULL,
    locked_until = NULL,
    reason = #{reason},
    updated_at = #{now},
    fencing_token = #{fencingToken}
WHERE id = #{id}
  AND status = 'HELD'
  AND fencing_token <= #{fencingToken}
