mapper.insert:
INSERT INTO signer_nonce_allocation(
    signer, nonce, status, locked_until, updated_at, created_at
)
VALUES(
    #{signer}, #{nonce}, 'HELD', #{lockedUntil}, #{updatedAt}, #{createdAt}
)
ON CONFLICT(signer, nonce)
DO UPDATE SET
    status = 'HELD',
    locked_until = #{lockedUntil},
    updated_at = #{updatedAt}
WHERE signer_nonce_allocation.status != 'CONSUMED'
