mapper.select:
SELECT next_local_nonce
FROM signer_nonce_state
WHERE signer = #{signer}

mapper.update:
UPDATE signer_nonce_state
SET next_local_nonce = #{newValue},
    fencing_token = #{fencingToken},
    updated_at = #{now}
WHERE signer = #{signer}
  AND next_local_nonce = #{expected}
  AND fencing_token <= #{fencingToken}

mapper.insert:
INSERT INTO signer_nonce_state(
    signer, next_local_nonce, fencing_token, updated_at, created_at
)
VALUES(
    #{signer}, #{nextLocalNonce}, 0, #{updatedAt}, #{createdAt}
)
ON CONFLICT(signer) DO NOTHING
