mapper.select:
SELECT id, submitter, nonce, status, lock_owner, locked_until AS reserved_until, tx_hash, reason, updated_at, created_at
FROM submitter_nonce_allocation
WHERE submitter = #{submitter} AND nonce = #{nonce}

mapper.update:
UPDATE submitter_nonce_allocation
SET status = 'RESERVED',
    lock_owner = #{lockOwner},
    locked_until = #{reservedUntil},
    updated_at = #{now}
WHERE submitter = #{submitter}
  AND nonce = #{nonce}
  AND status = 'RECYCLABLE'

mapper.select:
SELECT id, submitter, nonce, status, lock_owner, locked_until AS reserved_until, tx_hash, reason, updated_at, created_at
FROM submitter_nonce_allocation
WHERE submitter = #{submitter}
  AND status = 'RESERVED'
  AND locked_until IS NOT NULL
  AND locked_until < #{now}

mapper.update:
UPDATE submitter_nonce_allocation
SET status = 'USED',
    tx_hash = #{txHash},
    lock_owner = NULL,
    locked_until = NULL,
    updated_at = #{now}
WHERE submitter = #{submitter}
  AND nonce = #{nonce}
  AND status = 'RESERVED'
  AND lock_owner = #{lockOwner}

UPDATE submitter_nonce_allocation
SET status = 'RECYCLABLE',
    reason = #{reason},
    tx_hash = NULL,
    lock_owner = NULL,
    locked_until = NULL,
    updated_at = #{now}
WHERE submitter = #{submitter}
  AND nonce = #{nonce}
  AND status = 'RESERVED'
  AND lock_owner = #{lockOwner}
