mapper.select:
SELECT submitter, last_chain_nonce, next_local_nonce, updated_at, created_at
FROM submitter_nonce_state
WHERE submitter = #{submitter}

mapper.update:
UPDATE submitter_nonce_state
SET next_local_nonce = #{newNextLocalNonce},
    updated_at = #{now}
WHERE submitter = #{submitter}
  AND next_local_nonce = #{expectedNextLocalNonce}
